  `text` text NOT NULL COMMENT 'Текст заказа',
  `email` varchar(255) DEFAULT NULL COMMENT 'E-mail',
  `phone_number` varchar(12) NOT NULL COMMENT 'Номер телефона',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

