DROP TABLE IF EXISTS `reviews`;
CREATE TABLE `reviews` (
  `ip` varchar(39) NOT NULL COMMENT 'IP с которого написан отзыв',
  `name` varchar(255) NOT NULL COMMENT 'Имя',
  `tour_id` int(11) NOT NULL COMMENT 'Id экскурсии',
